package com.syntax.class01;

public class NergisOwnWork {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte box1 = 7;
		short box2 = 99;
		int box3 = 678;
		long box4 = 890374L;
		
		float decimal1 = 7.5F;
		double decimal2 = 45.8933;
		
		char character1 = 'A';
		char character2 = '%';
		
		boolean hot = true;
		boolean cold = false;
		
		System.out.println("The byte value is "+box1);
		System.out.println("The short value is "+box2);
		System.out.println("The int value is "+box3);
		System.out.println("The long value is "+box4);
		
		System.out.println("The value of float is "+decimal1);
		System.out.println("The value of double is "+decimal2);
		
		System.out.println("The value of char is "+character1);
		System.out.println("The value of char is "+character2);
		
		System.out.println("The value of boolean is "+hot);
		System.out.println("The value of boolean is "+cold);
	    

	}

}
