package com.syntax.class02;

public class homeworkNumber2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float d1=9.99f;
		double d2=99.99999;
		double sum=d1+d2;
		double difference=d1-d2;
		double product=d1*d2;
		double quotient=d1/d2;
		
		System.out.println("The addition of 2 numbers "+d1+" and "+d2+" is equal to "+sum);
		System.out.println("The multiplication of 2 numbers "+d1+" and "+d2+" is equal to "+product);
		System.out.println("The division of 2 numbers "+d1+" and "+d2+" is equal to "+quotient);
		System.out.println("The subtraction of 2 numbers "+d1+" and "+d2+" is equal to "+(difference));
		
		float square=3.9f;
		
		System.out.println("The square of the "+square+" is "+square*square);
		
		int width=5;
		int height=8;
		
		System.out.println("The perimeter of a rectangle with width "+width+" and height "+height+" equals to "+width*height);
		
	}

}
